$(document).ready(function(){$(".owl-carousel").owlCarousel({loop:!0,margin:10,dots:!0,nav:!1,responsiveClass:!0,responsive:{0:{items:1,nav:!0},600:{items:1,nav:!1},1e3:{items:1,nav:!0,loop:!0}}}),$(".navColapseNuevos").hide(),$(".arrow-up").hide(),$(".busqTitulo").click(function(){$(this).next(".navColapseNuevos").slideToggle(500),$(this).find(".arrow-up, .arrow-down").toggle(1)});var t=$("#horizontalTab");t.responsiveTabs({rotate:!1,startCollapsed:"false",collapsible:"accordion",setHash:!0,click:function(t,o){$(".info").html("Tab <strong>"+o.id+"</strong> clicked!")},activate:function(t,o){$(".info").html("Tab <strong>"+o.id+"</strong> activated!")},activateState:function(t,o){$(".info").html("Switched from <strong>"+o.oldState+"</strong> state to <strong>"+o.newState+"</strong> state!")}}),$("#start-rotation").on("click",function(){t.responsiveTabs("active")}),$("#enable-tab").on("click",function(){t.responsiveTabs("enable",3)}),$(".select-tab").on("click",function(){t.responsiveTabs("activate",$(this).val()),event.preventDefault()})});